import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import Card from "../UI/Card";

import "./Expenses.css";

const Expenses = ({ expenses, onFilterExpenses }) => {
  const [expenseYear, setExpenseYear] = useState("All");
  

  const expenseItem = (expenses) => {
    return expenses.map((expense) => (
      <ExpenseItem
        title={expense.title}
        date={expense.date}
        amount={expense.amount}
        key={expense.id}
      />
    ));
  };

  const expenseFilterYear = (expenseYearData) => {
    if (expenseYearData === "All") {
      setFilteredExpenses(expenses);
    } else {
      setExpenseYear(expenseYearData);
      const results = expenses.filter((expense) =>
        expense.date.toString().includes(expenseYearData)
      );
      setFilteredExpenses(results);
    }
  };


  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={expenseYear}
          onExpenseFilterYear={expenseFilterYear}
        />
        {filteredExpenses !== expenses
          ? expenseItem(filteredExpenses)
          : expenseItem(expenses)}
      </Card>
    </div>
  );
};

export default Expenses;
